        $date = new \DateTime();

        $day = (int)$date->format('d');
        $month = (int)$date->format('m');
        $year = (int)$date->format('Y');

        $seed = $year * 10000 + $month * 100 + $day;

        $hash = 0;
        foreach (str_split((string)$seed) as $char) {
            $hash = ($hash * 31 + ord($char)) & 0xFFFFFFFF;
        }

        $index = $hash % count($emojis) + 67;
        return $emojis[$index];
